import React, { useState, useEffect } from 'react';
import { FaBars } from 'react-icons/fa';
import { IconContext } from 'react-icons/lib';
import { animateScroll as scroll, scrollSpy } from 'react-scroll'
import {
	Nav,
	NavbarContainer,
	NavLogo,
	MobileIcon,
	NavMenu,
	NavItem,
	NavLinks,
	NavBtn,
	NavBtnLink
} from './NavbarElements';

const Navbar = ({ toggle }) => {
	const [scrollNav, setScrollNav] = useState(false)

	const changeNav = () => {
		if (window.scrollY >= 80) {
			setScrollNav(true)
		} else {
			setScrollNav(false)
		}
	}

	useEffect(() => {
		window.addEventListener('scroll', changeNav)
	}, [])

	const toggleHome = () => {
		scroll.scrollToTop();
	}

	let email = localStorage.getItem('email');
	console.log(email);
	return (
		<>
			<IconContext.Provider value={{ color: '#fff' }}>
				<Nav scrollNav={scrollNav}>
					<NavbarContainer>
						<NavLogo to="/" onClick={toggleHome}> logo </NavLogo>
						<MobileIcon onClick={toggle}>
							<FaBars />
						</MobileIcon>
						<NavMenu>
							<NavItem>
								<NavLinks to="about" smooth={true} duration={500} spy={true} exact='true' offset={-80}>About</NavLinks>
							</NavItem>
							<NavItem>
								<NavLinks to="discover" smooth={true} duration={500} spy={true} exact='true' offset={-80}>Discover</NavLinks>
							</NavItem>
							<NavItem>
								<NavLinks to="services" smooth={true} duration={500} spy={true} exact='true' offset={-80}>Services</NavLinks>
							</NavItem>
							<NavItem>
								<a className="sc-ciSkZP guXAji" href="/recipes" offset={-80}>Recipes</a>
							</NavItem>
							<NavItem>
								{email !== null ? <NavLinks onClick={() => { localStorage.removeItem('email'); window.location.reload(); }}> Log Out </NavLinks> : <a className="sc-ciSkZP guXAji" href="/signup" offset={-80}>Sign Up</a>}
							</NavItem>
						</NavMenu>
						<NavBtn>
							{email !== null ? <NavBtnLink to="/account"> Account </NavBtnLink> : <NavBtnLink to="/signin"> Sign In </NavBtnLink>}
						</NavBtn>
					</NavbarContainer>
				</Nav>
			</IconContext.Provider>
		</>
	)
}

export default Navbar;
